mapper.select:
SELECT COUNT(1) FROM sys_menu
deleted = 0
AND name LIKE CONCAT('%', #{name}, '%')
AND path LIKE CONCAT('%', #{path}, '%')
AND parentId = #{parentId}
AND isEnabled = #{isEnabled}

mapper.update:
UPDATE sys_menu
SET updatedAt = NOW(), isEnabled = #{isEnabled}
WHERE id = #{id} AND deleted = 0

UPDATE sys_menu
SET updatedAt = NOW(), isEnabled = #{isEnabled}
WHERE deleted = 0
AND id IN

    #{id}
