mapper.select:
SELECT
FROM sys_user WHERE userName = #{userName} AND deleted = 0
AND id != #{excludeId}

mapper.update:
UPDATE sys_user
SET updatedAt = NOW(), isEnabled = #{isEnabled}
WHERE id = #{id} AND deleted = 0

UPDATE sys_user
SET deleted = 1, updatedAt = NOW()
WHERE id = #{id}

UPDATE sys_user
SET updatedAt = NOW(), isEnabled = #{isEnabled}
WHERE deleted = 0
AND id IN

    #{id}
